import React, { useState } from "react";
import LayoutFunctionComponent from "../@types/layoutTypes/layoutFunctionComponent";
import AuthLayout from "../layouts/authLayout";
import { makeStyles } from "@material-ui/styles";
import { Card, CardContent, Typography, Box } from "@material-ui/core";




const Login: LayoutFunctionComponent = (props) => {
  const [selectedTab, setSelectedTab] = useState(0);

  function handleTabChange(event, value) {
    setSelectedTab(value);
  }

  return (
    <React.Fragment>
      <div
        className="d-flex justify-content-around  container"
        style={{
          height: "100vh",
          overflow: "hidden",
        }}
      >
        <div className="row">
          <div className="col-md-5">
            <div className="d-flex align-items-center justify-content-center relative">
              <img
                className="absolute w-100"
                src="static/loginBackground.png"
                alt="background"
              />
              <img className="m-auto" src="static/login.png" alt="logo" />
            </div>
          </div>
          <div className="col-md-7">
            <Card className="d-flex align-items-center pt-5 w-100 h-100 ">
              <CardContent className="d-flex flex-column align-items-center justify-content-center w-100 my-auto">
                <h3>Đăng nhập vào tài khoản ERP của bạn</h3>


                {/* <LoginTab /> */}
              </CardContent>
            </Card>
          </div>



        </div>

      </div>
      <style jsx>{`
          .relative{
            position: relative
          }
          .absolute {
            position: absolute
          }
          
        `}</style>
    </React.Fragment>
  );
};

Login.Layout = AuthLayout;
export default Login;
