import React, { useEffect, useRef, useState } from "react";
import {
  Button,
  Divider,
  Typography,
  InputAdornment,
  Icon,
  TextField,
} from "@material-ui/core";
import Formsy from "formsy-react";
import { useDispatch, useSelector } from "react-redux";
import Axios from "axios";
import { makeStyles } from "@material-ui/styles";
import Link from "next/link";



function LoginTab(props) {
  const dispatch = useDispatch();
  // const login = useSelector(({ auth }) => auth.login);

  const [isFormValid, setIsFormValid] = useState(false);
  const formRef = useRef(null);
  const [ip, setIp] = useState("");

  // useEffect(async () => {
  //   const data = await Axios.get(`https://www.cloudflare.com/cdn-cgi/trace`);
  //   const result = data.data.split("\n");
  //   const IpResult = result[2].slice(3);
  //   setIp(IpResult);
  // }, []);

  // useEffect(() => {
  //   if (login.error && (login.error.email || login.error.password)) {
  //     formRef.current.updateInputsWithError({
  //       ...login.error,
  //     });
  //     disableButton();
  //   }
  // }, [login.error]);

  function disableButton() {
    setIsFormValid(false);
  }

  function enableButton() {
    setIsFormValid(true);
  }

  function handleSubmit(model) {
    console.log(model);
  }

  return (
    <div className="pt-5 w-50">
      <Formsy
        onValidSubmit={handleSubmit}
        onValid={enableButton}
        onInvalid={disableButton}
        ref={formRef}
        className="d-flex flex-column justify-content-center align-items-center w-100"
      >
        <Typography variant="subtitle2" gutterBottom className="pb-5">
          IP : {ip}
        </Typography>
        {/* <TextField
          className="mb-5 w-100"
          type="text"
          name="database"
          label="Database"
          InputProps={{
            style: { height: "45px" },
            endAdornment: <InputAdornment position="end"></InputAdornment>,
          }}
          variant="outlined"
        /> */}

        <TextField
          className="mb-5 w-100"
          type="text"
          name="email"
          label="Tài khoản"

          InputProps={{
            style: { height: "45px" },
            endAdornment: <InputAdornment position="end"></InputAdornment>,
          }}
          variant="outlined"
          required
        />

        <TextField
          className="mb-5 w-100 "
          type="password"
          name="password"
          label="Mật khẩu"

          InputProps={{
            style: { height: "45px" },
            endAdornment: <InputAdornment position="end"></InputAdornment>,
          }}
          variant="outlined"
          required
        />

        <Button
          type="submit"
          variant="contained"
          color="primary"
          className="w-full mx-auto mt-16 normal-case bg-blue"
          aria-label="LOG IN"
          disabled={!isFormValid}
          value="legacy"
        >
          <span>ĐĂNG NHẬP</span>
        </Button>
        <div
          className="d-flex align-items-center justify-content-between w-100 pt-20"
          style={{ fontSize: 16 }}
        >
          <Link href="/register">Quên mật khẩu</Link>
          <Link href="/">Đăng ký</Link>
        </div>
      </Formsy>
    </div>
  );
}

export default LoginTab;
